mapper.select:
select count(*) from connectReport
where
report_no=#{report_no}
and reg_id =#{reg_id}
and REG_RESULT='1'

mapper.update:
update connectreport set
reg_result ='2'
where report_no=#{report_no}
and reg_id=#{reg_id}
and connect_no=#{connect_no}

update connectreport set
reg_result ='0',reg_cancel=#{reg_cancel}
where report_no=#{report_no}
and reg_id=#{reg_id}
and connect_no=#{connect_no}
