mapper.select:
		SELECT COUNT(*)
		FROM BOARD
		

		
			AND DEPARTNAME = #{category1}

SELECT BOARDNUM, TITLE, DEPARTNAME, WRITER, WRITERPOS, TO_CHAR(REGDATE,'yyyy-mm-dd') REGDATE, HIT , (SELECT COUNT(*) FROM BOARDFILE WHERE BOARDNUM=DS.BOARDNUM) FILECNT , (SELECT COUNT(*) FROM BOARDREPLY WHERE BOARDNUM=DS.BOARDNUM AND REDELETEFLAG='N') REPLYCNT FROM ( SELECT BOARDNUM, TITLE, DEPARTNAME, WRITER, WRITERPOS, REGDATE, HIT , ROW_NUMBER() OVER(ORDER BY BOARDNUM DESC) BOARDNUM_SEQ FROM BOARD
AND DEPARTNAME = #{category1}
) DS WHERE BOARDNUM_SEQ BETWEEN ${rowStart} AND ${rowEnd} ORDER BY BOARDNUM DESC

mapper.update:
UPDATE BOARD
SET TITLE=#{title}
, WRITER=#{writer}
, WRITERPOS=#{writerpos}
, CONTENT=#{content}
WHERE BRDDELETEFLAG='N'
AND BOARDNUM=#{boardnum}

UPDATE BOARD
SET HIT = HIT + 1
WHERE BOARDNUM=#{boardnum}

mapper.delete:
UPDATE BOARD
SET BRDDELETEFLAG='Y'
WHERE BOARDNUM=#{boardnum}

DELETE
FROM BOARDFILE
WHERE FILENUM IN (

	${item}

)

mapper.update:
UPDATE BOARDREPLY
SET REDELETEFLAG='Y'
WHERE RENUM=#{renum}

UPDATE BOARDREPLY
SET REMEMO=#{rememo}
WHERE RENUM=#{renum}
