mapper.update:
UPDATE PHOTO
   SET TITLE=#{title}
     , WRITER=#{writer}
     , WRITERPOS=#{writerpos}
 WHERE BRDDELETEFLAG='N'
   AND PHOTONUM=#{photonum}

mapper.delete:
UPDATE PHOTO
   SET BRDDELETEFLAG='Y' 
 WHERE PHOTONUM=#{photonum}


       DELETE 
         FROM PHOTOFILE
        WHERE PHOTOFILENUM IN (
             
                    ${item}
               
       )
